    <script>
        document.addEventListener('DOMContentLoaded', () => {
            const openBtn = document.querySelector('.open-btn');
            const overlay = document.querySelector('.overlay');
            const datePicker = document.querySelector('.date-picker');
            const closeIcon = document.querySelector('.close-icon');
            const clearDates = document.querySelector('.clear-dates');
            const saveBtn = document.querySelector('.save-btn');
            const loadMoreBtn = document.querySelector('.load-more');
            let selectedDates = [];
            let currentMonth = new Date().getMonth();
            let currentYear = new Date().getFullYear();

            const openDatePicker = () => {
                overlay.classList.add('active');
                setTimeout(() => {
                    datePicker.classList.add('active');
                }, 10);
                
                document.querySelector('._1fhpuic').style.display = "none";

            };

            const closeDatePicker = () => {
                datePicker.classList.remove('active');
                setTimeout(() => {
                    overlay.classList.remove('active');
                }, 300);
                document.querySelector('._1fhpuic').style.display = "block";
            };

            openBtn.addEventListener('click', openDatePicker);
            closeIcon.addEventListener('click', closeDatePicker);

            const renderCalendar = (month, year) => {
                const monthDiv = document.createElement('div');
                monthDiv.classList.add('month1');
                
                const monthYearDiv = document.createElement('div');
                monthYearDiv.classList.add('month-year1');
                monthYearDiv.textContent = `${new Date(year, month).toLocaleString('default', { month: 'long' })} ${year}`;
                monthDiv.appendChild(monthYearDiv);

                const calendar = document.createElement('div');
                calendar.classList.add('calendar1');
                const firstDay = new Date(year, month, 1).getDay();
                const daysInMonth = new Date(year, month + 1, 0).getDate();

                for (let i = 0; i < firstDay; i++) {
                    const emptyDiv = document.createElement('div');
                    emptyDiv.classList.add('disabled');
                    calendar.appendChild(emptyDiv);
                }

                for (let i = 1; i <= daysInMonth; i++) {
                    const dayDiv = document.createElement('div');
                    dayDiv.textContent = i;
                    dayDiv.addEventListener('click', () => selectDate(year, month, i));
                    calendar.appendChild(dayDiv);
                }

                monthDiv.appendChild(calendar);
                document.querySelector('.calendar-container1').appendChild(monthDiv);
            };

            const selectDate = (year, month, day) => {
                const date = new Date(year, month, day);
                if (selectedDates.length === 0 || (selectedDates.length === 1 && date > selectedDates[0])) {
                    selectedDates.push(date);
                } else {
                    selectedDates = [date];
                }
                updateSelectedDates();
            };

            const updateSelectedDates = () => {
                document.querySelectorAll('.calendar1 div').forEach(div => {
                    div.classList.remove('selected', 'range');
                });

                if (selectedDates.length > 0) {
                    const startDate = selectedDates[0];
                    const endDate = selectedDates[1] || startDate;

                    document.querySelectorAll('.calendar1 div').forEach(div => {
                        const divDate = new Date(startDate.getFullYear(), startDate.getMonth(), parseInt(div.textContent));
                        if (divDate >= startDate && divDate <= endDate) {
                            div.classList.add('range');
                        }
                        if (divDate.getTime() === startDate.getTime() || (selectedDates[1] && divDate.getTime() === selectedDates[1].getTime())) {
                            div.classList.remove('range');
                            div.classList.add('selected');
                        }
                    });

                    if (selectedDates.length === 2) {
                        const diffTime = Math.abs(selectedDates[1] - selectedDates[0]);
                        const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
                        if (diffDays < 2) {
                            alert('Minimum stay is 2 nights');
                            selectedDates.pop();
                            updateSelectedDates();
                        } else {
                            saveBtn.classList.add('active');
                            saveBtn.disabled = false;
                        }
                    } else {
                        saveBtn.classList.remove('active');
                        saveBtn.disabled = true;
                    }
                }
            };

            const clearSelectedDates = () => {
                selectedDates = [];
                updateSelectedDates();
            };

            const saveDates = () => {
                if (selectedDates.length < 2) {
                    alert('Please select at least two dates');
                    return;
                }

                // document.getElementById('date-container').innerHTML = `<span>${dateDisplay}</span>`;
                console.log(selectedDates[0])
                alert(`Dates selected: ${selectedDates.map(date => date.toDateString()).join(' - ')}`);

                 datePicker.classList.remove('active');
                setTimeout(() => {
                    overlay.classList.remove('active');
                }, 300);
                document.querySelector('._1fhpuic').style.display = "block";

            };

             // const selectedDates = [new Date('2023-09-04'), new Date('2023-09-07')];
             // displaySelectedDates(selectedDates);

            const loadMoreDates = () => {
                currentMonth++;
                if (currentMonth > 11) {
                    currentMonth = 0;
                    currentYear++;
                }
                renderCalendar(currentMonth, currentYear);
            };

            clearDates.addEventListener('click', clearSelectedDates);
            saveBtn.addEventListener('click', saveDates);
            loadMoreBtn.addEventListener('click', loadMoreDates);

            const today = new Date();
            renderCalendar(today.getMonth(), today.getFullYear());
            renderCalendar(today.getMonth() + 1, today.getFullYear());
        });
    </script>


