<script>
function initMap() {
    var mapOptions = {
        zoom: 12,
        center: { lat: -33.890542, lng: 151.274856 } // Default center
    };

    var map = new google.maps.Map(document.getElementById('map'), mapOptions);
    var map2 = new google.maps.Map(document.getElementById('map2'), mapOptions);

    var geocoder = new google.maps.Geocoder();
    var address = "<?=$fetch_property['city']?>, <?=$fetch_property['address']?>"; // Dynamic address from PHP

    geocodeAddress(geocoder, map, address);
    geocodeAddress(geocoder, map2, address);

    // Create custom controls
   // createCustomControls(map);
    //createCustomControls(map2);
}

function geocodeAddress(geocoder, resultsMap, address) {
    geocoder.geocode({ 'address': address }, function (results, status) {
        if (status === 'OK') {
            resultsMap.setCenter(results[0].geometry.location);
            var marker = new google.maps.Marker({
                map: resultsMap,
                position: results[0].geometry.location,
                icon: {
                    url: 'data:image/svg+xml;charset=UTF-8,' + encodeURIComponent(
                        '<svg xmlns="http://www.w3.org/2000/svg" width="48" height="48" viewBox="0 0 48 48"><circle cx="24" cy="24" r="24" fill="#FF5A5F"/><path d="M24 14l10 10v10h-6v-6h-8v6h-6v-10z" fill="#FFFFFF"/></svg>'
                    ),
                    scaledSize: new google.maps.Size(48, 48), // Adjust the size as needed 
                }
            });

            var country = getCountryFromGeocodeResult(results);
            var countryNameElems = document.querySelectorAll('.countryName');
            for (let elem of countryNameElems) {
                elem.innerText = country;
            }
            // document.querySelector('.countryName').innerText = country;

        } else {
            alert('Geocode was not successful for the following reason: ' + status);
        }
    });
}

function getCountryFromGeocodeResult(results) {
    for (var i = 0; i < results[0].address_components.length; i++) {
        var component = results[0].address_components[i];
        if (component.types.includes("country")) {
            return component.long_name;
        }
    }
    return null;
}

function createCustomControls(map) {
    var controlDiv = document.createElement('div');
    controlDiv.className = 'custom-map-controls';

    // Add Pegman control
    var pegmanDiv = document.createElement('div');
    pegmanDiv.innerHTML = '<img src="https://maps.gstatic.com/tactile/pane/default/pegman_sherlock.png" alt="Pegman">';
    controlDiv.appendChild(pegmanDiv);

    map.controls[google.maps.ControlPosition.TOP_LEFT].push(controlDiv);
}

// Initialize the map
google.maps.event.addDomListener(window, 'load', initMap);
</script>


