document.addEventListener("DOMContentLoaded", function () {

function calculateTotalCost() {
const nightlyRate = parseFloat(document.getElementById('nightlyRate').innerText);
const nightsText = document.getElementById('nightsTotalShowPrice').innerText;
const numberOfNights = parseInt(nightsText.match(/\d+/)[0]);

const cleaningFee = parseFloat(document.getElementById('cleaningFeeVal').value);
const secure_deposit = parseFloat(document.getElementById('secure_deposit').value);
const airbnbServiceFee = parseFloat(document.getElementById('airbnbServiceFee').value);
const trouristFee = parseFloat(document.getElementById('trouristFee').value);

const totalCost = nightlyRate * numberOfNights;
const totalAfterCost = totalCost + cleaningFee + secure_deposit +  airbnbServiceFee + trouristFee;
document.getElementById('totalPrice').innerText = `$${totalCost.toFixed(2)}`;
document.getElementById('totalBeforeTax').innerText = `$${totalAfterCost.toFixed(2)}`;
}

const displayLeft = document.querySelector(".display-left");
const daysLeft = document.querySelector(".container:nth-child(2) .days");
const selectedLeft = document.querySelector(".container:nth-child(2) .selected");

const displayRight = document.querySelector(".display-right");
const daysRight = document.querySelector(".container:nth-child(3) .from-today");
const selectedRange = document.querySelector(".container:nth-child(3) .selected-range");

const previous = document.querySelector(".left");
const next = document.querySelector(".right");

let date = new Date();
let year = date.getFullYear();
let month = date.getMonth();

let startDate = null;
let endDate = null;

function displayCalendar(display, days, offset = 0, isFromToday = false) {
const targetDate = new Date(year, month + offset, 1);
const firstDay = new Date(targetDate.getFullYear(), targetDate.getMonth(), 1);
const lastDay = new Date(targetDate.getFullYear(), targetDate.getMonth() + 1, 0);
const firstDayIndex = firstDay.getDay();
const numberOfDays = lastDay.getDate();

let formattedDate = targetDate.toLocaleString("en-US", {
month: "long",
year: "numeric",
});

display.innerHTML = `${formattedDate}`;
days.innerHTML = "";

for (let x = 1; x <= firstDayIndex; x++) {
const div = document.createElement("div");
days.appendChild(div);
}

for (let i = 1; i <= numberOfDays; i++) {
let div = document.createElement("div");
let currentDate = new Date(targetDate.getFullYear(), targetDate.getMonth(), i);
const today = new Date();
today.setHours(0, 0, 0, 0); // Normalize today's date to midnight

if (currentDate < today) {


div.classList.add("disabled");
} else {
div.dataset.date = currentDate.toDateString();
div.innerHTML = i;
days.appendChild(div);

if (
  currentDate.getFullYear() === new Date().getFullYear() &&
  currentDate.getMonth() === new Date().getMonth() &&
  currentDate.getDate() === new Date().getDate()
) {
  div.classList.add("current-date");
}
}
}

updateSelectedRange();
}

function updateSelectedRange() {
const allDays = document.querySelectorAll(".days div");

allDays.forEach((day) => {
day.classList.remove("current-date", "in-range");
});

if (startDate && endDate) {
const start = new Date(startDate);
const end = new Date(endDate);

allDays.forEach((day) => {
const dayDate = new Date(day.dataset.date);
if (dayDate >= start && dayDate <= end) {
  day.classList.add("in-range");
}
});
}

if (startDate) {
const startDay = document.querySelector(`div[data-date='${startDate}']`);
if (startDay) startDay.classList.add("current-date");
}

if (endDate) {
const endDay = document.querySelector(`div[data-date='${endDate}']`);
if (endDay) endDay.classList.add("current-date");
}
}

function displaySelected(days, selected, isRange = false) {
const dayElements = days.querySelectorAll("div");
dayElements.forEach((day) => {
day.addEventListener("click", (e) => {
const selectedDate = e.target.dataset.date;
if (selectedDate && !e.target.classList.contains("disabled")) {
	
  if (true) {
    if (!startDate || (startDate && endDate)) {
      startDate = selectedDate;
      endDate = null;
    } else {
      endDate = selectedDate;
      if (new Date(startDate) > new Date(endDate)) {
        [startDate, endDate] = [endDate, startDate];


      }
    }
    updateSelectedRange();
    updateSelectedElements();
  } else {
    startDate = selectedDate;
    updateSelectedRange();
     updateSelectedElements();
  }
}
});
});
}

