function formatDate(dateString) {
const date = new Date(dateString);
const day = date.getDate();
const month = date.getMonth() + 1;
const year = date.getFullYear();
return `${month}/${day}/${year}`;
}

function updateSelectedElements() {

if (startDate !== null && endDate !== null) {
document.getElementById('checkIn-book_it').value = formatDate(startDate);
document.getElementById('checkOut-book_it').value = formatDate(endDate);
document.getElementById('changeValueCheckin').innerHTML = formatDate(startDate);
document.getElementById('changeValueCheckout').innerHTML = formatDate(endDate);
const start = new Date(startDate);
const end = new Date(endDate);
const nights = Math.round((end - start) / (1000 * 60 * 60 * 24));
document.getElementById('daysLeft').innerHTML = `${nights} nights`;
document.getElementById('nightsTotalShowPrice').innerHTML = `${nights} nights`;
document.getElementById('datewFromTo').innerText = `${startDate} - ${endDate}`;
document.getElementById('showAfterCalendar').style = 'display:block';
document.getElementById('replaceTextButton').innerHTML = 'Reserve';
document.getElementById('removeAddTextPrice').style = 'display:none';
document.getElementById('priceTotalHeaderShow').style = 'display:block';
document.getElementById('pricePerNightHeaderShow').style = 'display:block';
document.getElementById('cleaningFeeVal').addEventListener('input', calculateTotalCost);
calculateTotalCost();
 $('#calendarApplicaiton').hide();
}
}

function slide(direction) {
daysLeft.classList.add(`slide-${direction}`);
daysRight.classList.add(`slide-${direction}`);

setTimeout(() => {
daysLeft.classList.remove(`slide-${direction}`);
daysRight.classList.remove(`slide-${direction}`);
}, 300);
}

displayCalendar(displayLeft, daysLeft);
displayCalendar(displayRight, daysRight, 1, true);

previous.addEventListener("click", () => {
if (new Date(year, month - 1) < new Date()) return;

month--;
if (month < 0) {
month = 11;
year--;
}
date.setMonth(month);
slide("left");

setTimeout(() => {
displayCalendar(displayLeft, daysLeft);
displayCalendar(displayRight, daysRight, 1, true);
displaySelected(daysLeft, selectedLeft);
displaySelected(daysRight, selectedRange, true);
}, 300);
});

next.addEventListener("click", () => {
month++;
if (month > 11) {
month = 0;
year++;
}
date.setMonth(month);
slide("right");

setTimeout(() => {
displayCalendar(displayLeft, daysLeft);
displayCalendar(displayRight, daysRight, 1, true);
displaySelected(daysLeft, selectedLeft);
displaySelected(daysRight, selectedRange, true);
}, 300);
});

displaySelected(daysLeft, selectedLeft);
displaySelected(daysRight, selectedRange, true);
updateSelectedElements();

