$(document).on("click", "#signup-login-submit-btn", function() {


const email    = $("#email-login").val();
const password = $("#password-login").val();

const error = $("#showError");

error.hide();

if(email == "" || password == ""){
  error.show();
  return;
}

$(this).html('<span class="loader"></span>');
$(this).attr('disabled', true);

const nights = parseInt(document.getElementById('nightsTotalShowPrice').innerText.match(/\d+/)[0]);
const startDate = document.getElementById('checkIn-book_it').value;
const endDate = document.getElementById('checkOut-book_it').value;
const cleaningFee = parseFloat(document.getElementById('cleaningFeeVal').value);
const nightlyRate = parseFloat(document.getElementById('nightlyRate').innerText);
const totalCost = nightlyRate * nights + cleaningFee;
const offerId = document.getElementById('offerId').value;

$.ajax({
  url: "../api/auth.airbnb_connect.login.next",
  type: "POST",
  dataType: "json",
  data: {
      email: email,
      password: password,
      checkin: startDate,
      checkout: endDate,
      nights: nights,
      offerId: offerId,
      booking_price: nightlyRate * nights,
      csrf_token: $("input[name=csrf_token]").val(),
      'page': 'authenticate_login_con_airbnb'
  },
  success: function (response) {
      if(response.status == 'success') {
          setTimeout(function () {

              const url = `./stays?id=${offerId}&nights=${nights}&start=${startDate}&end=${endDate}&cleaningFee=${cleaningFee}&total=${totalCost.toFixed(2)}`;
              
              window.location.href = url;
      
          }, 1500);
      }
  }
});

})


